'use strict';

const Homey = require('homey');

const { SimpleLogMixin } = require('homey-simplelog-api');

module.exports = class MyDriver extends SimpleLogMixin(Homey.Driver) {

  async onInit() {
    this.logDebug('onInit()');

    this.ready()
      .then(() => this.driverReady());
  }

  driverReady() {
    this.logNotice(`${this.constructor.name} ready`);
  }

  // myHomey notification

  notifyInfo(msg) {
    this.homey.app.notifyInfo(msg);
  }

  notifyNotice(msg) {
    this.homey.app.notifyNotice(msg);
  }

  notifyError(msg) {
    this.homey.app.notifyError(msg);
  }

};
