'use strict';

const Homey = require('homey');

module.exports = class MySimpleClass extends Homey.SimpleClass {

  // Singleton

  static getInstance(...args) {
    if (!this.instance) {
      this.instance = new this(...args);
    }
    return this.instance;
  }

  constructor(owner) {
    super();
    this.owner = owner;
    this.homey = this.owner.homey;
  }

  // myHomey logger

  logError(msg) {
    this.owner.logError(`${this.constructor.name} > ${msg}`);
  }

  logInfo(msg) {
    this.owner.logInfo(`${this.constructor.name} > ${msg}`);
  }

  logDebug(msg) {
    this.owner.logDebug(`${this.constructor.name} > ${msg}`);
  }

  // myHomey notification

  notifyInfo(msg) {
    this.homey.app.notifyInfo(msg);
  }

  notifyNotice(msg) {
    this.homey.app.notifyNotice(msg);
  }

  notifyError(msg) {
    this.homey.app.notifyError(msg);
  }

};
